terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
    random = {
      source  = "hashicorp/random"
      version = "~> 3.1"
    }
  }
  required_version = ">= 1.5.0"
}

provider "aws" {
  region = var.aws_region
}

resource "random_string" "suffix" {
  length  = 8
  special = false
  upper   = false
}

# Lambda function for MealMuse API
resource "aws_lambda_function" "app_api" {
  function_name = "MealMuseAPI-${random_string.suffix.result}"
  handler       = "index.handler"
  runtime       = "nodejs18.x"
  role          = aws_iam_role.lambda_exec.arn
  filename      = "api.zip"
  timeout       = 30
  memory_size   = 256

  environment {
    variables = {
      APP_NAME = "MealMuse"
      CORE_FEATURE = "AI meal planning"
      DYNAMODB_TABLE = aws_dynamodb_table.app_data.name
    }
  }

  tags = {
    Name = "MealMuseAPI"
    Environment = "Production"
  }
}

# IAM role for Lambda
resource "aws_iam_role" "lambda_exec" {
  name = "MealMuseLambdaRole-${random_string.suffix.result}"

  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [{
      Action = "sts:AssumeRole"
      Effect = "Allow"
      Principal = { Service = "lambda.amazonaws.com" }
    }]
  })
}

resource "aws_iam_role_policy_attachment" "lambda_basic" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

# DynamoDB table for app data
resource "aws_dynamodb_table" "app_data" {
  name           = "MealMuseData-${random_string.suffix.result}"
  billing_mode   = "PAY_PER_REQUEST"
  hash_key       = "id"

  attribute {
    name = "id"
    type = "S"
  }

  tags = {
    Name = "MealMuseData"
    Environment = "Production"
  }
}

# IAM policy for DynamoDB access
resource "aws_iam_role_policy" "lambda_dynamodb" {
  name = "MealMuseDynamoDBPolicy"
  role = aws_iam_role.lambda_exec.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Action = [
          "dynamodb:PutItem",
          "dynamodb:GetItem",
          "dynamodb:UpdateItem",
          "dynamodb:DeleteItem",
          "dynamodb:Query",
          "dynamodb:Scan"
        ]
        Resource = aws_dynamodb_table.app_data.arn
      }
    ]
  })
}

# S3 bucket for frontend hosting
resource "aws_s3_bucket" "app_frontend" {
  bucket = "mealmuse-frontend-${random_string.suffix.result}"

  tags = {
    Name = "MealMuseFrontend"
    Environment = "Production"
  }
}

resource "aws_s3_bucket_public_access_block" "app_frontend" {
  bucket = aws_s3_bucket.app_frontend.id

  block_public_acls       = false
  block_public_policy     = false
  ignore_public_acls      = false
  restrict_public_buckets = false
}

resource "aws_s3_bucket_policy" "app_frontend" {
  bucket = aws_s3_bucket.app_frontend.id
  depends_on = [aws_s3_bucket_public_access_block.app_frontend]

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Sid       = "PublicReadGetObject"
        Effect    = "Allow"
        Principal = "*"
        Action    = "s3:GetObject"
        Resource  = "${aws_s3_bucket.app_frontend.arn}/*"
      }
    ]
  })
}

resource "aws_s3_bucket_website_configuration" "app_frontend" {
  bucket = aws_s3_bucket.app_frontend.id

  index_document {
    suffix = "index.html"
  }

  error_document {
    key = "index.html"
  }
}

# REST API Gateway
resource "aws_api_gateway_rest_api" "app_api" {
  name        = "MealMuseAPI-${random_string.suffix.result}"
  description = "API for MealMuse"

  endpoint_configuration {
    types = ["REGIONAL"]
  }

  tags = {
    Name = "MealMuseAPI"
  }
}

resource "aws_api_gateway_resource" "app_resource" {
  rest_api_id = aws_api_gateway_rest_api.app_api.id
  parent_id   = aws_api_gateway_rest_api.app_api.root_resource_id
  path_part   = "{proxy+}"
}

resource "aws_api_gateway_method" "app_method" {
  rest_api_id   = aws_api_gateway_rest_api.app_api.id
  resource_id   = aws_api_gateway_resource.app_resource.id
  http_method   = "ANY"
  authorization = "NONE"
}

resource "aws_api_gateway_integration" "app_integration" {
  rest_api_id = aws_api_gateway_rest_api.app_api.id
  resource_id = aws_api_gateway_resource.app_resource.id
  http_method = aws_api_gateway_method.app_method.http_method

  integration_http_method = "POST"
  type                    = "AWS_PROXY"
  uri                     = aws_lambda_function.app_api.invoke_arn
}

resource "aws_api_gateway_deployment" "app_deployment" {
  depends_on = [
    aws_api_gateway_method.app_method,
    aws_api_gateway_integration.app_integration
  ]

  rest_api_id = aws_api_gateway_rest_api.app_api.id
  stage_name  = "prod"
}

resource "aws_lambda_permission" "api_gw" {
  statement_id  = "AllowExecutionFromAPIGateway"
  action        = "lambda:InvokeFunction"
  function_name = aws_lambda_function.app_api.function_name
  principal     = "apigateway.amazonaws.com"
  source_arn    = "${aws_api_gateway_rest_api.app_api.execution_arn}/*/*"
}

# Variables
variable "aws_region" {
  description = "AWS region for deployment"
  type        = string
  default     = "us-east-1"
}

# Outputs
output "api_gateway_url" {
  value = aws_api_gateway_deployment.app_deployment.invoke_url
  description = "API Gateway URL"
}

output "frontend_url" {
  value = "http://${aws_s3_bucket.app_frontend.bucket}.s3-website-${var.aws_region}.amazonaws.com"
  description = "Frontend website URL"
}

output "database_name" {
  value = aws_dynamodb_table.app_data.name
  description = "DynamoDB table name"
}
