provider "aws" {
  region = "us-west-2"
}

resource "aws_cognito_user_pool" "main" {
  name = "zeppelin_sneaker_user_pool"
}

resource "aws_api_gateway_rest_api" "main" {
  name = "zeppelin_sneaker_api"
}

resource "aws_lambda_function" "sneaker_service" {
  filename = "sneaker_service.zip"
  function_name = "sneaker_service"
  role = aws_iam_role.lambda_exec.arn
  handler = "index.handler"
  runtime = "nodejs12.x"
}

resource "aws_lambda_function" "order_service" {
  filename = "order_service.zip"
  function_name = "order_service"
  role = aws_iam_role.lambda_exec.arn
  handler = "index.handler"
  runtime = "nodejs12.x"
}

resource "aws_lambda_function" "notification_service" {
  filename = "notification_service.zip"
  function_name = "notification_service"
  role = aws_iam_role.lambda_exec.arn
  handler = "index.handler"
  runtime = "nodejs12.x"
}

resource "aws_rds_cluster" "main" {
  cluster_identifier = "zeppelin-sneaker-db"
  engine = "aurora-mysql"
  master_username = "admin"
  master_password = "password"
  skip_final_snapshot = true
}

resource "aws_s3_bucket" "main" {
  bucket = "zeppelin-sneaker-bucket"
  acl = "private"
}

resource "aws_iam_role" "lambda_exec" {
  name = "lambda_exec_role"

  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Action = "sts:AssumeRole"
        Effect = "Allow"
        Principal = {
          Service = "lambda.amazonaws.com"
        }
      },
    ]
  })
}

resource "aws_iam_role_policy_attachment" "lambda_exec_policy" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}
