provider "aws" {
  region = "us-west-2"
}

resource "aws_dynamodb_table" "task_db" {
  name           = "TaskDB"
  hash_key       = "id"
  billing_mode   = "PAY_PER_REQUEST"

  attribute {
    name = "id"
    type = "N"
  }
}

resource "aws_lambda_function" "task_lambda" {
  function_name = "TaskLambda"
  handler       = "index.handler"
  runtime       = "nodejs12.x"

  role = aws_iam_role.lambda_exec.arn

  filename = "lambda_function_payload.zip"

  environment {
    variables = {
      DYNAMODB_TABLE = aws_dynamodb_table.task_db.name
    }
  }
}

resource "aws_iam_role" "lambda_exec" {
  name = "lambda_exec"

  assume_role_policy = <<EOF
{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Action": "sts:AssumeRole",
      "Principal": {
        "Service": "lambda.amazonaws.com"
      },
      "Effect": "Allow",
      "Sid": ""
    }
  ]
}
EOF
}

resource "aws_api_gateway_rest_api" "api_gateway" {
  name        = "TaskAPI"
  description = "API for managing tasks"
}

resource "aws_api_gateway_resource" "task_resource" {
  rest_api_id = aws_api_gateway_rest_api.api_gateway.id
  parent_id   = aws_api_gateway_rest_api.api_gateway.root_resource_id
  path_part   = "task"
}

resource "aws_api_gateway_method" "task_post" {
  rest_api_id   = aws_api_gateway_rest_api.api_gateway.id
  resource_id   = aws_api_gateway_resource.task_resource.id
  http_method   = "POST"
  authorization = "NONE"
}

resource "aws_api_gateway_integration" "task_lambda" {
  rest_api_id = aws_api_gateway_rest_api.api_gateway.id
  resource_id = aws_api_gateway_resource.task_resource.id
  http_method = aws_api_gateway_method.task_post.http_method

  integration_http_method = "POST"
  type                    = "AWS_PROXY"
  uri                     = aws_lambda_function.task_lambda.invoke_arn
}

resource "aws_lambda_permission" "apigw" {
  statement_id  = "AllowExecutionFromAPIGateway"
  action        = "lambda:InvokeFunction"
  function_name = aws_lambda_function.task_lambda.function_name
  principal     = "apigateway.amazonaws.com"

  source_arn = "${aws_api_gateway_rest_api.api_gateway.execution_arn}/*/*"
}

output "api_gateway_url" {
  value = "${aws_api_gateway_rest_api.api_gateway.execution_arn}/*/*"
}
