provider "aws" {
  region = "us-west-2"
}

resource "aws_dynamodb_table" "recipeDB" {
  name           = "RecipeDB"
  billing_mode   = "PAY_PER_REQUEST"
  hash_key       = "id"

  attribute {
    name = "id"
    type = "N"
  }
}

resource "aws_db_instance" "userDB" {
  allocated_storage    = 20
  storage_type         = "gp2"
  engine               = "mysql"
  engine_version       = "5.7"
  instance_class       = "db.t2.micro"
  name                 = "UserDB"
  username             = "admin"
  password             = "password"
  parameter_group_name = "default.mysql5.7"
}

resource "aws_lambda_function" "mealPlanService" {
  filename      = "mealPlanService.zip"
  function_name = "MealPlanService"
  role          = aws_iam_role.iam_for_lambda.arn
  handler       = "mealPlanService.handler"
  source_code_hash = filebase64sha256("mealPlanService.zip")
  runtime       = "nodejs12.x"
}

resource "aws_lambda_function" "recipeService" {
  filename      = "recipeService.zip"
  function_name = "RecipeService"
  role          = aws_iam_role.iam_for_lambda.arn
  handler       = "recipeService.handler"
  source_code_hash = filebase64sha256("recipeService.zip")
  runtime       = "nodejs12.x"
