resource "aws_lambda_function" "groceryIntegration" {
  filename      = "groceryIntegration.zip"
  function_name = "GroceryIntegration"
  role          = aws_iam_role.iam_for_lambda.arn
  handler       = "groceryIntegration.handler"
  source_code_hash = filebase64sha256("groceryIntegration.zip")
