umlDiagrams.class:
classDiagram
    class Task {
        +String title
        +String description
        +DateTime dueDate
        +String status
    }
    class User {
        +String name
        +String email
    }
    class Project {
        +String name
        +String description
    }
    User "1" -- "*" Project : owns
    Project "1" -- "*" Task : contains
    User "1" -- "*" Task : assigns

umlDiagrams.entity:
erDiagram
    USER {
        string email PK
        string name
    }
    TASK {
        string id PK
        string title
        string description
        datetime dueDate
        string status
    }
    PROJECT {
        string id PK
        string name
        string description
    }
    USER_ASSIGNS_TASK {
        string user_email FK
        string task_id FK
    }
    USER_OWNS_PROJECT {
        string user_email FK
        string project_id FK
    }
    PROJECT_CONTAINS_TASK {
        string project_id FK
        string task_id FK
    }
    USER ||--o{ USER_OWNS_PROJECT : owns
    PROJECT ||--o{ USER_OWNS_PROJECT : is owned by
    USER ||--o{ USER_ASSIGNS_TASK : assigns
    TASK ||--o{ USER_ASSIGNS_TASK : is assigned
    PROJECT ||--|| PROJECT_CONTAINS_TASK : contains
    TASK ||--o{ PROJECT_CONTAINS_TASK : is contained

