terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
  required_version = ">= 1.5.0"
}

provider "aws" {
  region = "us-east-1"
}

resource "aws_s3_bucket" "meal_muse_data" {
  bucket = "meal-muse-data-bucket"
  acl    = "private"

  tags = {
    Name        = "MealMuseDataBucket"
    Environment = "Production"
  }
}

resource "aws_dynamodb_table" "user_profiles" {
  name           = "UserProfiles"
  billing_mode   = "PAY_PER_REQUEST"
  hash_key       = "UserID"

  attribute {
    name = "UserID"
    type = "S"
  }

  tags = {
    Name        = "UserProfilesTable"
    Environment = "Production"
  }
}

resource "aws_lambda_function" "meal_plan_service" {
  function_name = "MealPlanService"
  handler       = "meal_plan_service.handler"
  runtime       = "python3.9"
  role          = aws_iam_role.lambda_exec.arn

  # Assume the code is located in the same directory and is a zip file
  filename      = "meal_plan_service.zip"

  environment {
    variables = {
      DYNAMODB_TABLE = aws_dynamodb_table.user_profiles.name
    }
  }

  tags = {
    Name        = "MealPlanServiceFunction"
    Environment = "Production"
  }
}

resource "aws_iam_role" "lambda_exec" {
  name = "MealMuseLambdaExecutionRole"

  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Action    = "sts:AssumeRole"
        Effect    = "Allow"
        Principal = {
          Service = "lambda.amazonaws.com"
        }
      },
    ]
  })

  tags = {
    Name        = "LambdaExecutionRole"
    Environment = "Production"
  }
}

resource "aws_iam_role_policy_attachment" "lambda_policy" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

resource "aws_iam_role_policy_attachment" "dynamodb_policy" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/AmazonDynamoDBFullAccess"
}

variable "region" {
  description = "AWS region where resources will be deployed."
  type        = string
  default     = "us-east-1"
}

output "s3_bucket_name" {
  value       = aws_s3_bucket.meal_muse_data.id
  description = "The name of the S3 bucket for MealMuse data."
}

output "dynamodb_table_name" {
  value       = aws_dynamodb_table.user_profiles.name
  description = "The name of the DynamoDB table for user profiles."
}

output "lambda_function_name" {
  value       = aws_lambda_function.meal_plan_service.function_name
  description = "The name of the Lambda function for the meal plan service."
}

