import axios from 'axios';

const API_ENDPOINT = 'http://localhost:4000';

export const updateUserProfile = async (profile) => {
  try {
    const response = await axios.post(`${API_ENDPOINT}/user/profile`, profile);
    return response.data;
  } catch (error) {
    throw error;
  }
};

